
public class ArithmaticoperationsInt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x, y;
		x=175; y=225;
		System.out.println(" X = 175 \n Y = 225");
		System.out.println("\n Addition : X + Y = "+(x+y));
		System.out.println("\n Subtraction : X - Y = "+(x-y));
		System.out.println("\n Multiplication : X * Y = "+(x*y));
		System.out.println("\n Division : X / Y = "+(x/y));
		if (x>y) {
			System.out.println("\n"+x+" is greater than "+y+" : True");
		}
		else {
			System.out.println("\n"+x+" is greater than "+y+" : False");
		}
		

	}

}
